function countdown() {
    var matchDate = new Date("Dec 11, 2024 04:00:00").getTime(); // 设置比赛的日期和时间

    var now = new Date().getTime();
    var distance = matchDate - now;

    // 计算天、小时、分钟和秒
    var days = Math.floor(distance / (1000 * 60 * 60 * 24));
    var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    var seconds = Math.floor((distance % (1000 * 60)) / 1000);

    // 显示结果在元素中
    document.getElementById("date-countdown").innerHTML = days + "天 " + hours + "小时 "
    + minutes + "分钟 " + seconds + "秒 ";

    // 如果倒计时结束，可以进行一些操作
    if (distance < 0) {
        clearInterval(x);
        document.getElementById("date-countdown").innerHTML = "比赛已经开始";
    }
}

// 每秒更新倒计时
var x = setInterval(countdown, 1000);